import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from './login/Login';
import AddProject from './pages/AddProject'; // Assuming your AddProject page is here
import PMDashboard from './components/PMDashboard'; // Importing your PMDashboard
// You can adjust import paths if your folders are different

function App() {
    const [isLoggedIn, setIsLoggedIn] = React.useState(
        localStorage.getItem("isAuthenticated") === "true"
      );


  return (
    <Router>
      <Routes>
        {/* Default route - login page */}
        <Route path="/" element={<Login />} />

        {/* Dashboard route */}
        <Route path="/pm/dashboard" element={isLoggedIn ? <PMDashboard /> : <Navigate to="/" />} />

        {/* Add Project route */}
        <Route path="/addproject" element={isLoggedIn ? <AddProject /> : <Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
