import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditMilestone = () => {
  const { milestoneID } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    milestoneID: '',
    projectID: '',
    soWID: '',
    taskID: '',
    featureDescription: '',
    startDate: '',
    targetDate: '',
    currentStatus: '',
    currentPhase: '',
  });
  const [projects, setProjects] = useState([]);
  const [phases, setPhases] = useState([]);
  const [message, setMessage] = useState(null);
  const [loading, setLoading] = useState(true);

   const statusOptions = [
    { value: 'Completed', color: '#00FF00' },
    { value: 'Ongoing', color: '#FFA500' },
    { value: 'At Risk', color: '#FF0000' },
  ];

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const [projectRes, phaseRes, milestoneRes] = await Promise.all([
          axios.get('http://localhost:8080/project'),
          axios.get('http://localhost:8080/api/phases'),
          axios.get(`http://localhost:8080/milestones/${milestoneID}`)
        ]);

        const activeProjects = projectRes.data.filter(p => p.status === 'ACTIVE');
        setProjects(activeProjects);
        setPhases(phaseRes.data);

        const milestone = milestoneRes.data;
        setFormData({
          milestoneID: milestone.milestoneID,
          projectID: milestone.projectID,
          soWID: milestone.soWID,
          taskID: milestone.taskID,
          featureDescription: milestone.featureDescription,
          startDate: milestone.startDate,
          targetDate: milestone.targetDate,
          currentStatus: milestone.currentStatus,
          currentPhase: milestone.currentPhase,
        });
      } catch (err) {
        setMessage({ type: 'error', text: 'Failed to load data.' });
      } finally {
        setLoading(false);
      }
    };

    fetchInitialData();
  }, [milestoneID]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null);
    try {
      const payload = {
        ...formData,
        soWID: parseInt(formData.soWID),
        taskID: parseInt(formData.taskID),
        currentPhase: parseInt(formData.currentPhase),
      };
      await axios.put(`http://localhost:8080/milestones/update/${milestoneID}`, payload);
      setMessage({ type: 'success', text: 'Milestone updated successfully.' });
      setTimeout(() => navigate(`/view-milestones/${formData.projectID}`), 1500);
    } catch (err) {
      setMessage({ type: 'error', text: 'Failed to update milestone.' });
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">Edit Milestone</h2>
      {message && (
        <p className={`mb-4 text-center font-semibold ${message.type === 'success' ? 'text-green-600' : 'text-red-600'}`}>{message.text}</p>
      )}
      {loading ? <p>Loading...</p> : (
        <form onSubmit={handleSubmit} className="space-y-4">
          <select name="projectID" value={formData.projectID} onChange={handleChange} required className="w-full border px-3 py-2 rounded">
            <option value="">Select Project</option>
            {projects.map(p => (
              <option key={p.projectID} value={p.projectID}>{p.projectName}</option>
            ))}
          </select>
          <input type="number" name="soWID" value={formData.soWID} onChange={handleChange} placeholder="SoW ID" required className="w-full border px-3 py-2 rounded" />
          <input type="number" name="taskID" value={formData.taskID} onChange={handleChange} placeholder="Task ID" required className="w-full border px-3 py-2 rounded" />
          <textarea name="featureDescription" value={formData.featureDescription} onChange={handleChange} placeholder="Feature Description" required className="w-full border px-3 py-2 rounded resize-none" />
          <input type="date" name="startDate" value={formData.startDate} onChange={handleChange} required className="w-full border px-3 py-2 rounded" />
          <input type="date" name="targetDate" value={formData.targetDate} onChange={handleChange} required className="w-full border px-3 py-2 rounded" />
          <select name="currentStatus" value={formData.currentStatus} onChange={handleChange} required className="w-full border px-3 py-2 rounded">
            <option value="">Select Status</option>
            <option value="Completed">Completed</option>
            <option value="Ongoing">Ongoing</option>
            <option value="At Risk">At Risk</option>
          </select>
          <select name="currentPhase" value={formData.currentPhase} onChange={handleChange} required className="w-full border px-3 py-2 rounded">
            <option value="">Select Phase</option>
            {phases.map(p => (
              <option key={p.id} value={p.id}>{p.phaseName}</option>
            ))}
          </select>
          <div className="flex gap-4">
            <button type="submit" className="flex-1 bg-purple-700 text-white py-2 px-4 rounded hover:bg-purple-800">Save</button>
            <button type="button" onClick={() => navigate(`/view-milestones/${formData.projectID}`)} className="flex-1 bg-gray-300 text-gray-700 py-2 px-4 rounded hover:bg-gray-400">Cancel</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default EditMilestone;
