import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
i

function PMDashboard() {
    const [projects, setProjects] = useState([]);
    const [message, setMessage] = useState('');
   
    const dropdownRef = useRef(null);


    useEffect(() => {
        const fetchUser = async () => {
            try {
                const res = await axios.get('http://localhost:8080/api/login', {
                    withCredentials: true  // if using cookies/session
                });
                setManagerName(res.data.username);
            } catch (error) {
                console.error("Failed to fetch user info", error);
                setManagerName("Project Manager"); // fallback
            }
        };
        fetchUser();
    }, []);

    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/project');
                setProjects(response.data);
            } catch (error) {
                setMessage('Failed to fetch projects');
            }
        };
        fetchProjects();
    }, []);

    // Close dropdown on outside click
    useEffect(() => {
        const handleClickOutside = (e) => {
            if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
                setDropdownOpen(false);
            }
        };
        document.addEventListener('click', handleClickOutside);
        return () => document.removeEventListener('click', handleClickOutside);
    }, []);

  
    return (
        <div>
            <h1 className="text-2xl font-bold mb-4">Project List</h1>
            {message && <p className="text-red-500">{message}</p>}
            <ul className="space-y-4">
                {projects.map((project) => (
                    <li key={project.id} className="bg-white p-4 shadow rounded">
                        <h2 className="font-semibold text-lg">{project.name}</h2>
                        <p>Status: {project.status}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default PMDashboard;
