import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PMDashboard() {
  const [projects, setProjects] = useState([]);
  const [message, setMessage] = useState('');
  const [managerName, setManagerName] = useState('');

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get('http://localhost:8080/api/login', {
          withCredentials: true,
        });
        setManagerName(res.data.username);
      } catch (error) {
        console.error("Failed to fetch user info", error);
        setManagerName("Project Manager");
      }
    };
    fetchUser();
  }, []);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/projects');
        setProjects(response.data);
      } catch (error) {
        setMessage('Failed to fetch projects');
      }
    };
    fetchProjects();
  }, []);

  return (
    <div>
      <h2 className="text-2xl font-semibold text-gray-800 mb-6">Dashboard</h2>
      {message && <p className="text-red-500 mb-4">{message}</p>}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {projects.slice(0, 3).map((project) => (
          <div key={project.id} className="bg-white p-4 rounded shadow">
            <h3 className="text-lg font-semibold text-blue-600">{project.name}</h3>
            <p className="text-gray-700">Status: {project.status}</p>
            <p className="text-gray-700">Phase: {project.phase}</p>
            <p className="text-gray-700">Manager: {project.engineeringManager}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default PMDashboard;
