import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
 
const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
 
  const navigate = useNavigate();
 
  const handleLogin = async (event) => {
    event.preventDefault();
 
    try {
      const response = await axios.post("http://localhost:8080/api/login", {
        employeeEmail: email,
        password: password,
      });
 
      if (response.status === 200) {
        const employee = response.data;
        const role = employee.systemRoleId;
        let systemRoles = role.map((item) => item.systemRole);
        localStorage.setItem("currentRole", JSON.stringify(systemRoles));
        localStorage.setItem("isAuthenticated", "true");
        localStorage.setItem(
          "employeeRoleId",
          JSON.stringify(employee.employeeRoleId.employeeRoleId)
        );
        localStorage.setItem("employeeId", JSON.stringify(employee.employeeId));
        localStorage.setItem(
          "employeeName",
          JSON.stringify(employee.firstName + " " + employee.lastName)
        );
        if (systemRoles.length === 0) {
          alert("No roles assigned. Redirecting to error page.");
          navigate("/error");
        } else if (systemRoles.includes("Admin")) {
          navigate("/admin/dashboard");
        } else if (systemRoles.includes("Appraiser")) {
          navigate("/appraiser/dashboard");
        } else if (systemRoles.includes("Appraisee")) {
          navigate("/appraisee/dashboard");
        } else if (systemRoles.includes("Reviewer")) {
          navigate("/reviewer/dashboard");
        } else {
          setErrorMessage("Unauthorized role. Please contact admin.");
        }
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setErrorMessage("Invalid email or password. Please try again.");
      } else {
        setErrorMessage("An error occurred. Please try again later.");
      }
    }
  };
 
  return (
    <div className="flex h-screen bg-gradient-to-r">
      <div className="hidden lg:flex flex-1 flex-col justify-center items-center text-white px-12">
        <img src="bytestrone.png" alt="Welcome" className="rounded-xl" />
      </div>
 
      <div className="flex-1 flex flex-col justify-center px-12 bg-white rounded-l-3xl lg:rounded-l-none lg:shadow-lg">
        <h1 className="text-4xl font-bold mb-6 text-gray-800">Sign In</h1>
 
        {errorMessage && (
          <p className="text-red-500 text-sm mb-4">{errorMessage}</p>
        )}
 
        <form className="flex flex-col" onSubmit={handleLogin}>
          <label className="mb-2 font-medium text-gray-700">Email</label>
          <input
            type="email"
            placeholder="Enter your email"
            className="p-3 mb-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
 
          <label className="mb-2 font-medium text-gray-700">Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            className="p-3 mb-6 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
 
          <button
            type="submit"
            className="bg-indigo-600 text-white py-3 rounded-lg font-semibold hover:bg-indigo-700 transition duration-300"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};
 
export default LoginPage;
