import React from 'react';
import { useNavigate } from 'react-router-dom';

function Home() {
    const navigate = useNavigate();

    const handleLogout = () => {
        // Clear login state (you'll need a global solution later)
        navigate('/');
    };

    return (
        <div className="min-h-screen bg-gray-100 p-6">
            <header className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold text-gray-800">Dashboard</h1>
                <button
                    onClick={handleLogout}
                    className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 transition"
                >
                    Logout
                </button>
            </header>

            <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                <div className="bg-white p-6 rounded-lg shadow hover:shadow-md transition">
                    <h2 className="text-xl font-semibold text-indigo-600 mb-2">Projects</h2>
                    <p className="text-gray-600">Manage all your projects in one place.</p>
                    <button
                        onClick={() => navigate('/projects')}
                        className="mt-4 bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
                    >
                        View Projects
                    </button>
                </div>

                <div className="bg-white p-6 rounded-lg shadow hover:shadow-md transition">
                    <h2 className="text-xl font-semibold text-green-600 mb-2">Create Project</h2>
                    <p className="text-gray-600">Start a new project and assign tasks.</p>
                    <button
                        onClick={() => navigate('/create-project')}
                        className="mt-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                    >
                        New Project
                    </button>
                </div>

                <div className="bg-white p-6 rounded-lg shadow hover:shadow-md transition">
                    <h2 className="text-xl font-semibold text-blue-600 mb-2">Users</h2>
                    <p className="text-gray-600">Manage team members and their roles.</p>
                    <button
                        onClick={() => navigate('/users')}
                        className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
                    >
                        Manage Users
                    </button>
                </div>
            </section>
        </div>
    );
}

export default Home;
