import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function PMDashboard() {
    const [projects, setProjects] = useState([]);
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    // Fetch project data from the backend (adjust API endpoint as necessary)
    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/projects'); // Example endpoint
                setProjects(response.data);
            } catch (error) {
                setMessage('Failed to fetch projects');
            }
        };

        fetchProjects();
    }, []);

    return (
        <div className="p-6">
            <h2 className="text-2xl font-bold text-indigo-600">PM Dashboard</h2>
            {message && <p className="text-red-500 mt-4">{message}</p>}

            {/* Projects Overview */}
            <div className="mt-6">
                <h3 className="text-xl font-semibold text-gray-700">Active Projects</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-4">
                    {projects.filter(project => project.status === 'Active').map((project) => (
                        <div key={project.id} className="bg-white p-4 rounded-lg shadow">
                            <h4 className="font-semibold text-lg text-blue-600">{project.name}</h4>
                            <p className="text-gray-600">Status: {project.status}</p>
                            <p className="text-gray-600">Contributions: {project.totalContributions} ETH</p>
                            <button
                                onClick={() => navigate(`/projects/${project.id}`)}
                                className="mt-4 bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
                            >
                                View Project
                            </button>
                        </div>
                    ))}
                </div>
            </div>

            {/* Option to Create New Project */}
            <div className="mt-6">
                <button
                    onClick={() => navigate('/create-project')}
                    className="bg-green-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-green-700 transition"
                >
                    Create New Project
                </button>
            </div>
        </div>
    );
}

export default PMDashboard;
