import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { LogOut } from 'lucide-react';

const AdminDashboard = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const navigate = useNavigate();
  const avatar = '/images/avatar.png';
  const adminName = localStorage.getItem('username') || 'System Administrator';

  const [stats, setStats] = useState({
    totalUsers: 0,
    totalProjects: 0,
    activeProjects: 0,
    phases: 0,
    contractTypes: 0,
  });

  useEffect(() => {
    // Example: fetch stats from backend (replace URLs accordingly)
    const fetchStats = async () => {
      try {
        const res = await fetch('http://localhost:8080/admin/stats');
        const data = await res.json();
        setStats(data);
      } catch (err) {
        console.error('Failed to fetch admin stats', err);
      }
    };
    fetchStats();
  }, []);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setDropdownOpen(false);
      }
    };
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/');
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      {/* Top bar */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-gray-700">System Admin Dashboard</h1>
        <div className="relative" ref={dropdownRef}>
          <div
            className="flex items-center space-x-4 cursor-pointer"
            onClick={() => setDropdownOpen(!dropdownOpen)}
          >
            <span className="text-gray-800 font-medium">{adminName}</span>
            <img src={avatar} alt="avatar" className="w-10 h-10 rounded-full" />
          </div>
          {dropdownOpen && (
            <div className="absolute right-0 mt-2 w-40 bg-white rounded-lg shadow-lg z-50">
              <button
                onClick={handleLogout}
                className="flex items-center gap-2 w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100 rounded-lg"
              >
                <LogOut size={18} className="text-gray-600" />
                Logout
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Stats cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <StatCard title="Total Users" value={stats.totalUsers} />
        <StatCard title="Total Projects" value={stats.totalProjects} />
        <StatCard title="Active Projects" value={stats.activeProjects} />
        <StatCard title="Phases" value={stats.phases} />
        <StatCard title="Contract Types" value={stats.contractTypes} />
      </div>
    </div>
  );
};

const StatCard = ({ title, value }) => (
  <div className="bg-white p-6 rounded shadow hover:shadow-md transition">
    <h3 className="text-gray-500 text-sm mb-2">{title}</h3>
    <p className="text-2xl font-bold text-indigo-700">{value}</p>
  </div>
);

export default SADashboard;
