import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditMilestone = () => {
  const { milestoneID } = useParams(); // Extract milestoneID from route params
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    project: { projectID: '' },
    soWID: '',
    taskID: '',
    featureDescription: '',
    startDate: '',
    targetDate: '',
    currentStatus: '',
    currentPhaseID: '',
  });
  const [message, setMessage] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchMilestone = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://localhost:8080/milestones/${milestoneID}`);
        const milestone = response.data;
        setFormData({
          ...milestone,
          startDate: milestone.startDate ? new Date(milestone.startDate).toISOString().split('T')[0] : '',
          targetDate: milestone.targetDate ? new Date(milestone.targetDate).toISOString().split('T')[0] : '',
        });
      } catch (error) {
        const errorMsg = error.response?.data?.message || 'Failed to fetch milestone.';
        setMessage({ type: 'error', text: errorMsg });
      } finally {
        setLoading(false);
      }
    };

    if (milestoneID) {
      fetchMilestone();
    }
  }, [milestoneID]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'projectID') {
      setFormData({ ...formData, project: { ...formData.project, projectID: value } });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const validateForm = async () => {
    // Check required fields
    const requiredFields = [
      { key: 'project.projectID', label: 'Project ID' },
      { key: 'soWID', label: 'SoW ID' },
      { key: 'taskID', label: 'Task ID' },
      { key: 'featureDescription', label: 'Feature Description' },
      { key: 'targetDate', label: 'Target Date' },
      { key: 'currentStatus', label: 'Current Status' },
      { key: 'currentPhaseID', label: 'Current Phase' },
    ];

    for (let { key, label } of requiredFields) {
      const value = key.includes('project.') ? formData.project[key.split('.')[1]] : formData[key];
      if (!value) {
        setMessage({ type: 'error', text: `${label} is required.` });
        return false;
      }
    }

    // Validate no duplicate SoW ID or Task ID within the same project
    try {
      const response = await axios.get(`http://localhost:8080/milestones/project/${formData.project.projectID}`);
      const milestones = response.data || [];
      const currentMilestone = milestones.find((m) => m.milestoneID === parseInt(milestoneID));
      const otherMilestones = milestones.filter((m) => m.milestoneID !== parseInt(milestoneID));

      if (otherMilestones.some((m) => m.soWID === formData.soWID)) {
        setMessage({ type: 'error', text: 'SoW ID already exists for this project.' });
        return false;
      }
      if (otherMilestones.some((m) => m.taskID === formData.taskID)) {
        setMessage({ type: 'error', text: 'Task ID already exists for this project.' });
        return false;
      }
    } catch (error) {
      setMessage({ type: 'error', text: 'Failed to validate SoW ID and Task ID.' });
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage(null);

    if (!(await validateForm())) {
      setLoading(false);
      return;
    }

    try {
      await axios.put(`http://localhost:8080/milestones/update/${milestoneID}`, formData);
      setMessage({ type: 'success', text: 'Milestone updated successfully!' });
      setTimeout(() => {
        navigate(`/view-milestones/${formData.project.projectID}`);
      }, 1500);
    } catch (error) {
      const errorMsg = error.response?.data?.message || 'Failed to update milestone.';
      setMessage({ type: 'error', text: errorMsg });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="max-w-md w-full mx-auto p-6 bg-white rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">Edit Milestone</h2>
        {message && (
          <p className={`text-center font-semibold ${message.type === 'error' ? 'text-red-600' : 'text-green-600'} mb-4`}>
            {message.text}
          </p>
        )}
        {loading && <p className="text-center text-gray-600">Loading...</p>}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-700 font-medium mb-1">Project ID</label>
            <input
              type="number"
              name="projectID"
              placeholder="Project ID"
              value={formData.project.projectID}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-1">SoW ID</label>
            <input
              type="number"
              name="soWID"
              placeholder="SoW ID"
              value={formData.soWID}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-1">Task ID</label>
            <input
              type="number"
              name="taskID"
              placeholder="Task ID"
              value={formData.taskID}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-1">Feature Description</label>
            <textarea
              name="featureDescription"
              placeholder="Feature Description"
              value={formData.featureDescription}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none h-20"
              required
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-1">Start Date</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-1">Target Date</label>
            <input
              type="date"
              name="targetDate"
              value={formData.targetDate}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-1">Current Status</label>
            <input
              type="text"
              name="currentStatus"
              placeholder="Current Status"
              value={formData.currentStatus}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-1">Current Phase</label>
            <input
              type="text"
              name="currentPhaseID"
              placeholder="Current Phase"
              value={formData.currentPhaseID}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <button
            type="submit"
            disabled={loading}
            className={`w-full p-3 text-white rounded-lg transition-colors ${
              loading ? 'bg-blue-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'
            }`}
          >
            Save
          </button>
        </form>
      </div>
    </div>
  );
};

export default EditMilestone;
