import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from './login/Login';
import Home from "./pages/Home";
import MainLayout from './layout/MainLayout';
import PMDashboard from './pm/PMDashboard';
import AddProject from './pm/AddProject';
import ViewProjects from "./pm/ViewProjects";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <Router>
      <Routes>
        {/* Public route - Login */}
        <Route path="/" element={<Login setIsLoggedIn={setIsLoggedIn} />} />

        {/* Optional Home route */}
        <Route path="/home" element={<Home />} />

        {/* Protected PM routes under /pm */}
        <Route path="/pm" element={<MainLayout />}>
          <Route
            path="dashboard"
            element={isLoggedIn ? <PMDashboard /> : <Navigate to="/" />}
          />
          <Route
            path="viewprojects"
            element={isLoggedIn ? <ViewProjects /> : <Navigate to="/" />}
          />
          <Route
            path="addproject"
            element={isLoggedIn ? <AddProject /> : <Navigate to="/" />}
          />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
