import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { LogOut } from 'lucide-react';

// Random avatar generator
const getRandomAvatar = () => {
    const avatars = [
        'https://i.pravatar.cc/150?img=1',
        'https://i.pravatar.cc/150?img=2',
        'https://i.pravatar.cc/150?img=3',
        'https://i.pravatar.cc/150?img=4',
    ];
    return avatars[Math.floor(Math.random() * avatars.length)];
};

function PMDashboard() {
    const [projects, setProjects] = useState([]);
    const [message, setMessage] = useState('');
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);
    const navigate = useNavigate();
    const [managerName, setManagerName] = useState('');
    const [avatar] = useState(getRandomAvatar());

    useEffect(() => {
        const fetchUser = async () => {
            try {
                const res = await axios.get('http://localhost:8080/api/login', {
                    withCredentials: true  // if using cookies/session
                });
                setManagerName(res.data.username);
            } catch (error) {
                console.error("Failed to fetch user info", error);
                setManagerName("Project Manager"); // fallback
            }
        };
        fetchUser();
    }, []);

    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/projects');
                setProjects(response.data);
            } catch (error) {
                setMessage('Failed to fetch projects');
            }
        };
        fetchProjects();
    }, []);

    // Close dropdown on outside click
    useEffect(() => {
        const handleClickOutside = (e) => {
            if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
                setDropdownOpen(false);
            }
        };
        document.addEventListener('click', handleClickOutside);
        return () => document.removeEventListener('click', handleClickOutside);
    }, []);

    const handleLogout = () => {
        localStorage.removeItem("role");
        localStorage.removeItem("username");
        navigate('/');
    };

    return (
        <div className="flex min-h-screen bg-gray-100">
            {/* Sidebar */}
            <div className="w-64 bg-gray-800 text-white p-6">
                <h2 className="text-2xl font-bold mb-8">Welcome, Naveenn</h2>
                <ul>
                    <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer">Dashboard</li>
                    <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer">Projects</li>
                    <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer">Settings</li>
                    <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer">Reports</li>
                </ul>
            </div>

            {/* Main Content */}
            <div className="flex-1 p-6">
                {/* Top Bar */}
                <div className="flex items-center justify-between mb-6">
                    <div className="flex items-center">
                        <img src="/bytestrone.png" alt="Company Logo" className="h-10 mr-4" />
                    </div>
                    <div className="relative" ref={dropdownRef}>
                        <div
                            className="flex items-center space-x-4 cursor-pointer"
                            onClick={() => setDropdownOpen(!dropdownOpen)}
                        >
                            <span className="text-gray-800 font-medium">{managerName}</span>
                            <img
                                src={avatar}
                                alt="avatar"
                                className="w-10 h-10 rounded-full"
                            />
                        </div>
                        {dropdownOpen && (
                            <div className="absolute right-0 mt-2 w-40 bg-white rounded-lg shadow-lg z-50">
                                <button
                                    onClick={handleLogout}
                                    className="flex items-center gap-2 w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100 rounded-lg"
                                >
                                    <LogOut size={18} className="text-gray-600" />
                                    Logout
                                </button>
                            </div>
                        )}
                    </div>
                </div>

                {/* Project Cards */}
                <div>
                    <h3 className="text-xl font-semibold text-gray-700">Active Projects</h3>
                    {message && <p className="text-red-500 mt-2">{message}</p>}
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-4">
                        {projects.filter(p => p.status === 'Active').map(project => (
                            <div key={project.id} className="bg-white p-4 rounded-lg shadow">
                                <h4 className="text-lg font-semibold text-blue-600">{project.name}</h4>
                                <p className="text-gray-600">Status: {project.status}</p>
                                <p className="text-gray-600">Contributions: {project.totalContributions} ETH</p>
                                <button
                                    onClick={() => navigate(`/projects/${project.id}`)}
                                    className="mt-4 bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
                                >
                                    View Project
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default PMDashboard;
