import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { LogOut } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const ViewProjects = () => {
    const { id } = useParams();
    const [projects, setProjects] = useState([]);
    const [selectedProject, setSelectedProject] = useState(null);
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);
    const navigate = useNavigate();
    const managerName = localStorage.getItem('username') || 'Project Manager';
    const avatar = '/images/avatar.png';

    useEffect(() => {
        fetchAllProjects();
    }, []);

    const fetchAllProjects = async () => {
        setSelectedProject(null); // reset detail view
        try {
            const res = await axios.get(`http://localhost:8080/project/${id}`);
            setProjects(res.data);
        } catch (err) {
            setMessage('Failed to load projects');
        }
    };

    const fetchProjectById = async (id) => {
        setLoading(true);
        try {
            const res = await axios.get(`http://localhost:8080/project/${id}`);
            setSelectedProject(res.data);
        } catch (err) {
            setMessage('Failed to load project details');
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        localStorage.clear();
        navigate('/');
    };

    useEffect(() => {
        const handleClickOutside = (e) => {
            if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
                setDropdownOpen(false);
            }
        };
        document.addEventListener('click', handleClickOutside);
        return () => document.removeEventListener('click', handleClickOutside);
    }, []);

    return (
        <div className="flex min-h-screen bg-gray-100">
            {/* Sidebar */}
            <div className="w-64 bg-gray-800 text-white p-6">
                <h2 className="text-2xl font-bold mb-8">Welcome, {managerName}</h2>
                <ul>
                    <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer" onClick={() => navigate('/pmdashboard')}>
                        Dashboard
                    </li>
                    <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer" onClick={fetchAllProjects}>
                        Projects
                    </li>
                    <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer">Reports</li>
                </ul>
            </div>

            {/* Main Content */}
            <div className="flex-1 p-6">
                {/* Top Bar */}
                <div className="flex items-center justify-between mb-6">
                    <img src="/bytestrone.png" alt="Company Logo" className="h-10" />
                    <div className="relative" ref={dropdownRef}>
                        <div
                            className="flex items-center space-x-4 cursor-pointer"
                            onClick={() => setDropdownOpen(!dropdownOpen)}
                        >
                            <span className="text-gray-800 font-medium">{managerName}</span>
                            <img src={avatar} alt="avatar" className="w-10 h-10 rounded-full" />
                        </div>
                        {dropdownOpen && (
                            <div className="absolute right-0 mt-2 w-40 bg-white rounded-lg shadow-lg z-50">
                                <button
                                    onClick={handleLogout}
                                    className="flex items-center gap-2 w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100 rounded-lg"
                                >
                                    <LogOut size={18} className="text-gray-600" />
                                    Logout
                                </button>
                            </div>
                        )}
                    </div>
                </div>

                {/* View Mode */}
                {loading ? (
                    <p>Loading...</p>
                ) : selectedProject ? (
                    // Detail View
                    <div className="bg-white p-6 rounded shadow">
                        <h2 className="text-2xl font-bold text-indigo-700 mb-4">{selectedProject.name}</h2>
                        <p className="text-gray-700 mb-2"><strong>Status:</strong> {selectedProject.status}</p>
                        <p className="text-gray-700 mb-2"><strong>Phase:</strong> {selectedProject.phase}</p>
                        <p className="text-gray-700 mb-2"><strong>Manager:</strong> {selectedProject.engineeringManager}</p>
                        <p className="text-gray-700 mb-4"><strong>Description:</strong> {selectedProject.description}</p>
                        <button
                            onClick={fetchAllProjects}
                            className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold py-2 px-4 rounded"
                        >
                            ← Back to Projects
                        </button>
                    </div>
                ) : (
                    // List View
                    <>
                        <div className="flex justify-between items-center mb-6">
                            <h2 className="text-2xl font-semibold text-gray-700">Projects</h2>
                            <button
                                onClick={() => navigate('/addproject')}
                                className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-md font-medium"
                            >
                                + Create Project
                            </button>
                        </div>

                        {message && <p className="text-red-500">{message}</p>}

                        {projects.length === 0 ? (
                            <p className="text-gray-600">No projects found. Click “Create Project” to add one.</p>
                        ) : (
                            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                                {projects.map((project) => (
                                    <div key={project.id} className="bg-white p-4 rounded shadow">
                                        <h3 className="text-lg font-semibold text-blue-600">{project.name}</h3>
                                        <p className="text-gray-700">Status: {project.status}</p>
                                        <p className="text-gray-700">Manager: {project.engineeringManager}</p>
                                        <button
                                            className="mt-3 bg-indigo-500 text-white px-3 py-2 rounded hover:bg-indigo-600"
                                            onClick={() => fetchProjectById(project.id)}
                                        >
                                            View Details
                                        </button>
                                    </div>
                                ))}
                            </div>
                        )}
                    </>
                )}
            </div>
        </div>
    );
};

export default ViewProjects;
