import React, {useState} from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from './login/Login';
import Home from "./pages/Home";
import MainLayout from './layout/MainLayout';
import PMDashboard from './pm/PMDashboard';
import AddProject from './pm/AddProject'; // Assuming your AddProject page is here 
import ViewProjects from "./pm/ViewProjects";
import EditProject from "./pm/EditProject";
import ViewMilestones from "./pm/ViewMilestone";
import AddMilestones from "./pm/AddMilestone";
// You can adjust import paths if your folders are different

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);


  return (
    <Router>
      <Routes>
        {/* Default route - login page */}
        <Route path="/" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/home" element={<Home />} />
        <Route element={<MainLayout />}>
          <Route path="/pmdashboard" element={<PMDashboard />} />
          <Route path="/viewprojects" element={<ViewProjects />} />
          <Route path="/addproject" element={isLoggedIn ? <AddProject /> : <Navigate to="/" />} />
          <Route path="/editproject/:id" element={<EditProject />} />
          <Route path="/viewprojects/view-milestone/:projectID" element={<ViewMilestones />} />
          <Route path="/add-milestone/:projectID" element={<AddMilestones />} />
        </Route>
        </Routes>
    </Router>
  );
}

export default App;
