import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import { LogOut } from "lucide-react";
import { useUser } from "../UserContext"; // Adjust path as needed

export default function ViewMilestones() {
  const { user } = useUser();
  const managerName = user.name;
  const avatar = user.avatar;

  const { projectID } = useParams();
  const navigate = useNavigate();
  const [milestones, setMilestones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    loadMilestones();

    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [projectID]);

  const loadMilestones = async () => {
    try {
      const result = await axios.get(`http://localhost:8080/milestones/project/${projectID}`);
      setMilestones(result.data || []);
    } catch (error) {
      setError("Failed to load milestones. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (milestoneID) => {
    navigate(`/edit-milestone/${milestoneID}`);
  };

  const handleDelete = async (milestoneID) => {
    if (window.confirm("Are you sure you want to delete this milestone?")) {
      try {
        await axios.delete(`http://localhost:8080/milestones/delete/${milestoneID}`);
        setMilestones(milestones.filter((m) => m.milestoneID !== milestoneID));
      } catch (error) {
        alert("Failed to delete milestone. Please try again.");
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/login");
  };

  if (loading) return <h2 className="text-center mt-10 text-lg text-gray-600">Loading milestones...</h2>;
  if (error) return <h2 className="text-center mt-10 text-lg text-red-600">{error}</h2>;

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-gray-800 text-white p-6">
        <h2 className="text-2xl font-bold mb-8">Welcome, {managerName}</h2>
        <ul>
          <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer" onClick={() => navigate('/pmdashboard')}>
            Dashboard
          </li>
          <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer" onClick={() => navigate('/viewprojects')}>
            Projects
          </li>
          <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer" onClick={() => navigate('/view-milestones')}>
            Milestone
          </li>
          <li className="mb-4 hover:bg-gray-700 p-2 rounded cursor-pointer">Reports</li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-6">
        {/* Top Bar */}
        <div className="flex items-center justify-between mb-6">
          <img src="/bytestrone.png" alt="Company Logo" className="h-10" />
          <div className="relative" ref={dropdownRef}>
            <div
              className="flex items-center space-x-4 cursor-pointer"
              onClick={() => setDropdownOpen(!dropdownOpen)}
            >
              <span className="text-gray-800 font-medium">{managerName}</span>
              <img src={avatar} alt="avatar" className="w-10 h-10 rounded-full" />
            </div>
            {dropdownOpen && (
              <div className="absolute right-0 mt-2 w-40 bg-white rounded-lg shadow-lg z-50">
                <button
                  onClick={handleLogout}
                  className="flex items-center gap-2 w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100 rounded-lg"
                >
                  <LogOut size={18} className="text-gray-600" />
                  Logout
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Milestone View */}
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-semibold text-gray-700">Milestones of Project {projectID}</h2>
          <button
            onClick={() => navigate('/add-milestone')}
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-md font-medium"
          >
            + Create Milestone
          </button>
        </div>

        {milestones.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {milestones.map((milestone) => (
              <div key={milestone.milestoneID} className="bg-white rounded-lg shadow-md p-4">
                <h4 className="text-lg font-semibold text-indigo-700 mb-2">{milestone.featureDescription || "No Description"}</h4>
                <p className="text-gray-700"><strong>Start Date:</strong> {milestone.startDate ? new Date(milestone.startDate).toLocaleDateString() : "N/A"}</p>
                <p className="text-gray-700"><strong>Target Date:</strong> {milestone.targetDate ? new Date(milestone.targetDate).toLocaleDateString() : "N/A"}</p>
                <p className="text-gray-700"><strong>Status:</strong> {milestone.currentStatus || "N/A"}</p>
                <p className="text-gray-700"><strong>SoW ID:</strong> {milestone.soWID || "N/A"}</p>
                <p className="text-gray-700"><strong>Task ID:</strong> {milestone.taskID || "N/A"}</p>
                <div className="flex gap-2 mt-4">
                  <button
                    onClick={() => handleEdit(milestone.milestoneID)}
                    className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded text-sm"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(milestone.milestoneID)}
                    className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-600">No milestones available for the project.</p>
        )}
      </div>
    </div>
  );
}
